<script>
import firebase from 'firebase/app'

export default {
  components: {},
  data() {
    return {}
  },
  computed: {
    isLoggedIn() {
      return !!this.$store.state.user
    },
    user() {
      return this.$store.state.user
    },
  },
  methods: {
    signOut() {
      firebase
        .auth()
        .signOut()
        .then(() => {
          this.$router.push({ name: 'signin' })
        })
        // eslint-disable-next-line no-console
        .catch((error) => console.log(error))
    },
  },
}
</script>

